import { computed, ref, onMounted, watch } from "vue";
import { useStore } from "vuex";
import { mdiAccountMultiple, mdiCartOutline, mdiChartTimelineVariant, mdiFinance, mdiMonitorCellphone } from "@mdi/js";
import MainSection from "@/components/MainSection.vue";
import TitleBar from "@/components/TitleBar.vue";
import HeroBar from "@/components/HeroBar.vue";
import CardWidget from "@/components/CardWidget.vue";
import CardComponent from "@/components/CardComponent.vue";
import ClientsTable from "@/components/ClientsTable.vue";
import Notification from "@/components/Notification.vue";
import TitleSubBar from "@/components/TitleSubBar.vue";
export default {
  setup(__props, {
    expose
  }) {
    expose();
    const store = useStore();
    const titleStack = computed(() => {
      const orgName = store.getters.selectedOrganization?.name || "";
      return ["Admin", "Dashboard", orgName];
    });
    const selectedOrganization = computed(() => store.getters.selectedOrganization || {});
    const organizationJobs = computed(() => selectedOrganization.value?.jobs || []);
    const selectedJob = ref(null);
    const selectedJobApplications = computed(() => {
      if (!selectedJob.value) return [];
      return selectedJob.value.applications || [];
    });
    const selectedJobApplicationsCount = computed(() => selectedJobApplications.value.length);

    // Initialize data
    onMounted(() => {
      // Make sure organizations are initialized
      if (!store.state.userOrganizations.length) {
        store.dispatch("initializeOrganizations");
      }
      // Set first job as default when component mounts or organization changes
      if (organizationJobs.value.length > 0) {
        selectedJob.value = organizationJobs.value[0];
      }
    });

    // Watch for organization changes to update selected job
    watch(() => selectedOrganization.value, newOrg => {
      if (newOrg?.jobs?.length > 0) {
        selectedJob.value = newOrg.jobs[0];
      } else {
        selectedJob.value = null;
      }
    });
    const __returned__ = {
      store,
      titleStack,
      selectedOrganization,
      organizationJobs,
      selectedJob,
      selectedJobApplications,
      selectedJobApplicationsCount,
      computed,
      ref,
      onMounted,
      watch,
      useStore,
      mdiAccountMultiple,
      mdiCartOutline,
      mdiChartTimelineVariant,
      mdiFinance,
      mdiMonitorCellphone,
      MainSection,
      TitleBar,
      HeroBar,
      CardWidget,
      CardComponent,
      ClientsTable,
      Notification,
      TitleSubBar
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
