import { createStore } from "vuex";
import axios from "axios";
import * as styles from "@/styles.js";

// Cache configuration
const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes in milliseconds
const cache = {
  data: new Map(),
  timestamps: new Map(),
};

// Batch update handling
let batchTimeout = null;
const BATCH_DELAY = 100; // ms

const batchUpdates = (commit, updates) => {
  if (batchTimeout) clearTimeout(batchTimeout);

  batchTimeout = setTimeout(() => {
    updates.forEach((update) => {
      commit("basic", update);
    });
    batchTimeout = null;
  }, BATCH_DELAY);
};

export default createStore({
  state: {
    /* Styles */
    lightBorderStyle: styles.basic.lightBorder,
    lightBgStyle: styles.basic.lightBg,
    asideStyle: styles.basic.aside,
    asideBrandStyle: styles.basic.asideBrand,
    asideMenuCloseLgStyle: styles.basic.asideMenuCloseLg,
    asideMenuLabelStyle: styles.basic.asideMenuLabel,
    asideMenuItemStyle: styles.basic.asideMenuItem,
    asideMenuItemActiveStyle: styles.basic.asideMenuItemActive,
    asideMenuItemInactiveStyle: styles.basic.asideMenuItemInactive,
    asideSubmenuListStyle: styles.basic.asideSubmenuList,
    navBarItemLabelStyle: styles.basic.navBarItemLabel,
    navBarItemLabelHoverStyle: styles.basic.navBarItemLabelHover,
    navBarItemLabelActiveColorStyle: styles.basic.navBarItemLabelActiveColor,
    navBarMenuListUpperLabelStyle: styles.basic.navBarMenuListUpperLabel,
    tableTrStyle: styles.basic.tableTr,
    tableTrOddStyle: styles.basic.tableTrOdd,
    overlayStyle: styles.basic.overlay,

    /* User */
    userName: null,
    userEmail: null,
    userAvatar: null,

    /* organization */
    userOrganizations: [],
    selectedOrganizationIndex: 0,
    selectedOrganizationId: null,

    /* fullScreen - fullscreen form layout (e.g. login page) */
    isFullScreen: false,

    /* Aside */
    isAsideMobileExpanded: false,
    isAsideLgActive: false,
    isAsideCollapsed: false,

    /* Dark mode - hardcoded to true */
    darkMode: true,

    /* Sample data (commonly used) */
    clients: [],
    history: [],
  },
  getters: {
    selectedOrganization: (state) => {
      return state.userOrganizations.find(
        (org) => org.id === state.selectedOrganizationId
      );
    },
    getSelectedOrganization: (state) => {
      return state.userOrganizations.find(
        (org) => org.id === state.selectedOrganizationId
      );
    },
    selectedOrganizationApplications: (state, getters) => {
      const org = getters.selectedOrganization;
      if (!org) return [];
      // Flatten all applications from all jobs
      return org.jobs.reduce((allApps, job) => {
        return allApps.concat(job.applications || []);
      }, []);
    },
    selectedOrganizationIndex: (state) => {
      return state.selectedOrganizationIndex;
    },
    selectedOrganizationId: (state) => {
      return state.selectedOrganizationId;
    },
    getCachedData: (state) => (key) => {
      const timestamp = cache.timestamps.get(key);
      if (timestamp && Date.now() - timestamp < CACHE_DURATION) {
        return cache.data.get(key);
      }
      return null;
    },
  },
  mutations: {
    /* A fit-them-all commit */
    basic(state, payload) {
      state[payload.key] = payload.value;
    },

    /* User */
    user(state, payload) {
      if (payload.name) {
        state.userName = payload.name;
      }
      if (payload.email) {
        state.userEmail = payload.email;
      }
      if (payload.avatar) {
        state.userAvatar = payload.avatar;
      }
    },

    setUserOrganizations(state, payload) {
      state.userOrganizations = payload.organizations;
      // Set first organization as selected if available
      if (payload.organizations && payload.organizations.length > 0) {
        state.selectedOrganizationId = payload.organizations[0].id;
        state.selectedOrganizationIndex = 0;
      } else {
        state.selectedOrganizationId = null;
        state.selectedOrganizationIndex = null;
      }
    },
    setSelectedOrganization(state, payload) {
      state.selectedOrganizationId = payload.id;
      state.selectedOrganizationIndex = payload.index;
    },
    invalidateCache(state, key) {
      cache.data.delete(key);
      cache.timestamps.delete(key);
    },
  },
  actions: {
    setStyle({ commit }) {
      // Apply basic style by default
      const style = styles.basic;
      document.body.className = style.body;
      document.documentElement.className = style.html;

      // Apply each style to state
      for (const key in style) {
        if (!["body", "html"].includes(key)) {
          commit("basic", {
            key: `${key}Style`,
            value: style[key],
          });
        }
      }
    },

    asideMobileToggle({ commit, state }, payload = null) {
      const isShow = payload !== null ? payload : !state.isAsideMobileExpanded;

      document
        .getElementById("app")
        .classList[isShow ? "add" : "remove"]("ml-60", "lg:ml-0");
      document.documentElement.classList[isShow ? "add" : "remove"](
        "m-clipped"
      );

      commit("basic", {
        key: "isAsideMobileExpanded",
        value: isShow,
      });
    },

    asideLgToggle({ commit, state }, payload = null) {
      commit("basic", {
        key: "isAsideLgActive",
        value: payload !== null ? payload : !state.isAsideLgActive,
      });
    },

    asideCollapseToggle({ commit, state }, payload = null) {
      const isCollapsed = payload !== null ? payload : !state.isAsideCollapsed;
      commit("basic", {
        key: "isAsideCollapsed",
        value: isCollapsed,
      });
    },

    fullScreenToggle({ commit }, value) {
      commit("basic", { key: "isFullScreen", value });
      document.documentElement.classList[value ? "add" : "remove"](
        "full-screen"
      );
    },

    darkMode({ commit }) {
      // Always true
      document.documentElement.classList.add("dark");
      commit("basic", {
        key: "darkMode",
        value: true,
      });
    },

    async fetch({ commit, getters }, payload) {
      // Check cache first
      const cachedData = getters.getCachedData(payload);
      if (cachedData) {
        commit("basic", {
          key: payload,
          value: cachedData,
        });
        return;
      }

      try {
        const response = await axios.get(`data-sources/${payload}.json`);
        if (response.data && response.data.data) {
          // Update cache
          cache.data.set(payload, response.data.data);
          cache.timestamps.set(payload, Date.now());

          commit("basic", {
            key: payload,
            value: response.data.data,
          });
        }
      } catch (error) {
        console.warn(`Failed to load ${payload} data:`, error);
      }
    },

    batchUpdate({ commit }, updates) {
      batchUpdates(commit, updates);
    },

    initializeOrganizations({ commit }) {
      // Mock data for organizations - in real app this would be an API call
      const mockOrganizations = [
        {
          id: 1,
          currentPlan: "Free",
          name: "Organization 1",
          jobs: [
            {
              id: "swe-1ab2",
              name: "Software Engineering",
              description:
                "We are looking for a software engineer with 3 years of experience in React and Node.js",
              applications: ["jake", "todd", "frank", "curry"],
            },
            {
              id: "swe-1ab3",
              name: "Nurse",
              description:
                "We are looking for a nurse with 3 years of experience in hospital settings",
              applications: ["will", "jason"],
            },
            {
              id: "swe-1ab4",
              name: "Teacher  ",
              description:
                "We are looking for a teacher with 3 years of experience in school settings",
              applications: ["toots", "fatty", "helen"],
            },
          ],
        },
        {
          id: 2,
          currentPlan: "Pro",
          name: "Organization 2",
          jobs: [
            {
              id: "swe-1ab5",
              name: "Software Engineering",
              description:
                "We are looking for a software engineer with 3 years of experience in React and Node.js",
              applications: ["brad", "toddy"],
            },
            {
              id: "swe-1ab6",
              name: "Software Engineering",
              description:
                "We are looking for a software engineer with 3 years of experience in React and Node.js",
              applications: ["frederick", "capp"],
            },
            {
              id: "swe-1ab7",
              name: "Software Engineering",
              description:
                "We are looking for a software engineer with 3 years of experience in React and Node.js",
              applications: ["jake", "todd"],
            },
          ],
        },
      ];

      // Use batch update for multiple changes
      const updates = [
        { key: "userOrganizations", value: mockOrganizations },
        { key: "selectedOrganizationId", value: mockOrganizations[0].id },
        { key: "selectedOrganizationIndex", value: 0 },
      ];

      batchUpdates(commit, updates);
    },
  },
  modules: {},
});
