const sectionBgBase = "bg-gradient-to-tr";
export const sectionBgLogin = `${sectionBgBase} from-electric-teal via-bright-indigo to-vivid-coral`;
export const sectionBgLoginDark = `${sectionBgBase} from-electric-teal via-bright-indigo to-vivid-coral`;
export const sectionBgError = `${sectionBgBase} from-hot-pink via-vivid-coral to-sunburst-orange`;
export const sectionBgErrorDark = `${sectionBgBase} from-hot-pink via-vivid-coral to-sunburst-orange`;

export const colorsBg = {
  white: "bg-white text-deep-space",
  light: "bg-cloud-gray text-deep-space",
  success: "bg-electric-green text-white",
  danger: "bg-hot-pink text-white",
  warning: "bg-sunburst-orange text-white",
  info: "bg-azure-blue text-white",
  primary: "bg-electric-teal text-white",
};

export const colorsBgHover = {
  white: "hover:bg-cloud-gray",
  light: "hover:bg-gray-200",
  success: "hover:bg-opacity-90",
  danger: "hover:bg-opacity-90",
  warning: "hover:bg-opacity-90",
  info: "hover:bg-opacity-90",
  primary: "hover:bg-opacity-90",
};

export const colorsBorders = {
  white: "border-cloud-gray",
  light: "border-cloud-gray dark:border-dark-surface",
  success: "border-electric-green",
  danger: "border-hot-pink",
  warning: "border-sunburst-orange",
  info: "border-azure-blue",
  primary: "border-electric-teal",
};

export const colorsText = {
  white: "text-black dark:text-gray-100",
  light: "text-gray-700 dark:text-gray-400",
  success: "text-emerald-600",
  danger: "text-red-600",
  warning: "text-yellow-600",
  info: "text-blue-600",
};

export const colorsOutline = {
  white: [colorsText.white, colorsBorders.white],
  light: [colorsText.light, colorsBorders.light],
  success: [colorsText.success, colorsBorders.success],
  danger: [colorsText.danger, colorsBorders.danger],
  warning: [colorsText.warning, colorsBorders.warning],
  info: [colorsText.info, colorsBorders.info],
};

export const colorsOutlineHover = {
  white: "hover:bg-gray-100 hover:text-gray-900 dark:hover:text-gray-900",
  light: "hover:bg-gray-100 hover:text-gray-900 dark:hover:text-gray-900",
  success: "hover:bg-emerald-700 hover:text-white",
  danger: "hover:bg-red-700 hover:text-white",
  warning: "hover:bg-yellow-700 hover:text-white",
  info: "hover:bg-blue-700 hover:text-white",
};

export const getButtonColor = (color, isOutlined, hasHover) => {
  const base = [
    isOutlined ? colorsText[color] : colorsBg[color],
    colorsBorders[color],
  ];

  if (hasHover) {
    base.push(isOutlined ? colorsOutlineHover[color] : colorsBgHover[color]);
  }

  return base;
};
